import { Component } from '@angular/core';
import { AfterViewInit } from '@angular/core';
declare var anime: any;              // declare like this



@Component({
  selector: 'app-presentation-component',
  templateUrl: './presentation-component.component.html',
  styleUrls: ['./presentation-component.component.css']
})
export class PresentationComponentComponent  implements AfterViewInit {

  ngAfterViewInit(): void {
    // Wrap every letter in a span
const textWrapper = document.querySelector('.an-1');
    textWrapper!.innerHTML = textWrapper!.textContent!.replace(/\S/g, "<span class='letter'>$&</span>");

anime.timeline({loop: true})
.add({
  targets: '.an-1 .letter',
  scale: [4,1],
  opacity: [0,1],
  translateZ: 0,
  easing: "easeOutExpo",
  duration: 950,
  delay: (el:any, i:any) => 70*i
}).add({
  targets: '.an-1',
  opacity: 0,
  duration: 1000,
  easing: "easeOutExpo",
  delay: 1000
  });


  }


}

<div class="row" id="conteneur">
    <div class="col">
        <h2>Je suis<span class="" style="color:darkorange"> KONAN KOUAME AIME FABRICE</span></h2>
        <h4><span class="an-1" style="color:dark;"> DATA SCIENTIST, DEVELOPPEUR WEB ET MOBILE</span></h4>
        <h4> Actuellement étudiant en <span style="color:blue">MASTER INTELLIGENCE ARTIFICIELLE ET BIG DATA </span>. Diplômé d’une <span style="color:blue">LICENCE PROFESSIONNELLE</span> en systèmes informatiques et génie logiciel, j’ai acquis diverses compétences grâce à ma formation et à mes activités.</h4>
    </div>
    <div class="col">
        <div class="conteneur1">
            <h1>Presentation</h1>
            <p>Fabrice</p>
        </div>
    </div>
</div>
