import { Component } from '@angular/core';
import { AfterViewInit } from '@angular/core';
declare var anime: any;              // declare like this



@Component({
  selector: 'app-presentation-component',
  templateUrl: './presentation-component.component.html',
  styleUrls: ['./presentation-component.component.css']
})
export class PresentationComponentComponent  implements AfterViewInit {
  cheminImage:any="url/Prensent.png"
  ngAfterViewInit(): void {
    // Wrap every letter in a span
const textWrapper = document.querySelector('.an-1');
    textWrapper!.innerHTML = textWrapper!.textContent!.replace(/\S/g, "<span class='letter'>$&</span>");

anime.timeline({loop: true})
.add({
  targets: '.an-1 .letter',
  scale: [4,1],
  opacity: [0,1],
  translateZ: 0,
  easing: "easeOutExpo",
  duration: 950,
  delay: (el:any, i:any) => 70*i
}).add({
  targets: '.an-1',
  opacity: 0,
  duration: 1000,
  easing: "easeOutExpo",
  delay: 1000
  });


  }


}

<div class="row" id="conteneur">
    <div class="col">
        <h2><span style="color:dark;font-family: Cambria, Cochin, Georgia, Times, 'Times New Roman', serif;">Je suis</span> <span class="" style="color:darkorange"><strong> KONAN KOUAME AIME FABRICE</strong></span></h2>
        <h4><strong> <span class="an-1" style="color:dark;font-family: Cambria, Cochin, Georgia, Times, 'Times New Roman', serif;"><strong> DATA SCIENTIST, DEVELOPPEUR WEB ET MOBILE</strong></span></strong></h4>
        <h5 style="color:dark;font-family: Cambria, Cochin, Georgia, Times, 'Times New Roman', serif;"> Actuellement étudiant en <span style="color:dark;font-family: Cambria, Cochin, Georgia, Times, 'Times New Roman', serif;"><strong> MASTER INTELLIGENCE ARTIFICIELLE ET BIG DATA </strong></span>. Diplômé d’une <span style="color:dark;font-family: Cambria, Cochin, Georgia, Times, 'Times New Roman', serif;"><strong> LICENCE PROFESSIONNELLE</strong></span> en systèmes informatiques et génie logiciel, j’ai acquis diverses compétences grâce à ma formation et à mes activités.</h5>
        <h5 style="color:dark;font-family: Cambria, Cochin, Georgia, Times, 'Times New Roman', serif;"><strong>Trouvez moi sur:</strong> </h5>
    </div>
    <div class="col">
        <div class="conteneur1">
            <img src="assets\Images\Prensent.png" alt="">
            
           
        </div>
    </div>
</div>
